def plot_with_errors(x, y, x_err, name):
    plt.figure(figsize=(30, 20))
    plt.plot(x,y)
    plt.errorbar(x, y, xerr=x_err, fmt='o', ecolor='r', capsize=5, label='Данные с ошибками')
    plt.title(name, fontsize = 30)
    plt.xlabel('Speed', fontsize = 20)
    plt.ylabel('Position', fontsize = 20)
    plt.grid()
    plt.xticks(fontsize = 20)
    plt.yticks(fontsize = 20)
    plt.savefig(name + ".pdf", format='pdf', dpi=300)
    plt.show()

